import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import { CardList } from './components/card-list/card-list.component';
import { SearchBox } from './components/search-box/search-box.component';

class App extends Component { //shortcut rcc

  constructor(props) { //shortcut con
    super(props);
    this.state = {
      monsters: [],
      searchField: ''
    };
  };
  componentDidMount() { //shortcut cdm, componentDidMount: show element on DOM first.
    // get API
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(users => this.setState({ monsters: users }));
  }

  render() {
    const { monsters, searchField } = this.state;
    const filteredMonsters = monsters.filter(monster =>
      monster.name.toLowerCase().includes(searchField.toLowerCase())
    )

    return (
      <div className="App">
        <SearchBox placeholder="search monsters" onChange={e=>{this.setState({
          searchField:e.target.value
        })}}/>
        <CardList monsters={filteredMonsters} />
      </div>
    );
  }
}

export default App;
